import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Recipe () {

const [images, setImages] = useState([]);

  useEffect(() => {
    getImages();
  }, []);

  const recipeName = useParams()

  const getImages = async () => {
    const response = await fetch(`https://www.themealdb.com/api/json/v1/1/search.php?s=${recipeName.recipe}`)
    const data = await response.json()
    console.log(data.meals)
    setImages(data.meals)
  }
    return (
        <div>
            <section className="recipeMeals">

      {images.map((recipe) => {
          const {strMeal, strMealThumb, strInstructions, strArea} = recipe

          return (

            <div class="max-w-2xl bg-white border border-gray-200 rounded-lg shadow-md dark:bg-gray-800 dark:border-gray-700 m-auto">
                    <img class="rounded-t-lg" src={strMealThumb} alt="" />
                <div class="p-5">
                        <h5 class="mb-2 text-[3rem] font-bold tracking-tight text-gray-900 dark:text-white text-center">{strMeal}</h5>
                    <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">{strInstructions}</p>
                </div>
            </div>

          )
        })}
       </section>
        </div>

    )
}

export default Recipe;
