import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';

function Recipes() {
    const [images, setImages] = useState([]);

  useEffect(() => {
    getImages();
  }, []);

  const getImages = async () => {
    const response = await fetch(`https://www.themealdb.com/api/json/v1/1/categories.php`)
    const data = await response.json()
    setImages(data.categories)
  }

    return(
        <section id="Recipes" className="relative py-24 bg-slate-100">
{/* NAVIGATION */}
          <div className="navbar flex flex-row justify-between items-center mx-10 py-5 text-black">
              <div className="navbar-title text-3xl font-bold text-green-600">
                  <h1>Sprinkle & Dash</h1>
              </div>
              <div className="navbar-links">
                  <ul className="flex space-x-8 text-2xl">
                    <li><a href='#' className="hover:text-green-600 transition duration-200">Home</a></li>
                    <li><a href='#About' className="hover:text-green-600 transition duration-200">About Us</a></li>
                    <li><a href='#' className="hover:text-green-600 transition duration-200">Recipes</a></li>
                    <li><a href='#' className="hover:text-green-600 transition duration-200">Random Recipe</a></li>
                  </ul>
              </div>
                  </div>
        <div className="font-bold text-center text-4xl">
            <h1 className="uppercase text-green-600">Choose Your Category</h1>
        </div>
{/* Map through and return categories and images */}
        <div className="category-container flex justify-center flex-wrap">
            {images.map((recipe) => {
                const {strCategory, strCategoryThumb} = recipe

                return (
                    <div className="map-container flex inline-flex place-content-center mt-12">
                        <Link to={'/categories/' + strCategory}>
                          <div className="category-info space-x-4 text-slate-700 bg-white rounded-2xl p-5 mx-2 border border-slate-300 hover:scale-105 duration-300 hover:cursor-pointer">
                            <h2 className="text-2xl text-center font-bold mb-3">{strCategory}</h2>
                            <img src={strCategoryThumb} alt={strCategory} />
                          </div>
                        </Link>
                    </div>
                )
            })}
        </div>
      </section>
    )
}

export default Recipes;
