import React, { useState, useEffect } from "react";

function Recipes() {
    const [images, setImages] = useState([]);

  useEffect(() => {
    getImages();
  }, []);

  const getImages = async () => {
    const response = await fetch(`https://www.themealdb.com/api/json/v1/1/categories.php`)
    const data = await response.json()
    console.log(data.categories)
    setImages(data.categories)
  }

    return(
        <section id="Recipes" className="relative py-24 text-white bg-green-600">
        <div className="font-bold text-center text-4xl">
            <h1 className="uppercase">Choose Your Category</h1>
        </div>
{/* Map through and return categories and images */}
        {images.map((recipe) => {
            const {strCategory, strCategoryThumb} = recipe

            return (
                    <div className="category-container  flex inline-flex justify-center mt-12">
                        <div className="category-info">
                            <h2 className="text-2xl text-center font-bold mb-3">{strCategory}</h2>
                            <img src={strCategoryThumb} alt={strCategory} />
                        </div>
                    </div>
            )
        })}
      </section>
    )
}

export default Recipes;
