import React, { useState, useEffect } from "react";
import { Link, useParams } from 'react-router-dom';

const CategoryMeals = () => {

    const [images, setImages] = useState([]);

  useEffect(() => {
    getImages();
  }, []);

  const catName = useParams()
  console.log({catName})

  const getImages = async () => {
    const response = await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${catName}`)
    const data = await response.json()
    setImages(data.meals)
  }

    return (
        <section className="catMeals">

      {images.map((recipe) => {
          const {strMeal, strMealThumb} = recipe

          return (
            <article className="catMealContainer" key={strMeal}>
              <Link to={'/recipe/' + strMeal}>
              <h2>{strMeal}</h2>
              <img src={strMealThumb} alt={strMeal} />
              </Link>
            </article>
          )
        })}
       </section>
     );
    }

export default CategoryMeals;
